import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = { 
      position : 'main',
      loading : true,
      person : null,
      currentPerson : {
        name : null,
        email: null,
        age : null
      }
    }

    this.changePositionToUser = this.changePositionToUser.bind(this);
    this.changePositionToMain = this.changePositionToMain.bind(this);
  }

  async componentDidMount() {
    const url = "http://localhost:8000/users";

    axios.get(url)
    .then(res => {
      this.setState({
        loading : false,
        person: res.data
      })
      //console.log(res.data)
    })
    .catch(err => console.log(err));

  }

  changePositionToUser(){
    this.setState({position : 'user'});
  }

  changePositionToMain(){
    this.setState({position : 'main'});
  }

  render() {
    const userInfo = this.state.person ;
    return (
      <div className = 'App'>
        <nav>
          <h1>Welcome to Crud-App</h1>
          {
              this.state.position === 'main' ?
              <div>
                  <p className = 'to-the-left' onClick = {this.changePositionToUser}>Check all User</p>
              </div>
              :
              <div>
                  <p className = 'to-the-left' onClick = {this.changePositionToMain}> Back to Main Page</p>
              </div>


          };
        </nav>
        <main>
          {
            this.state.position === 'main' ? 
            <div className = 'content'>
              <form onSubmit= {this.onSubmitFormButton}>
                <label >Full Name</label>
                <input type="text" id="fname" name="firstname" placeholder="Your Full-Name.." />

                <label >Email</label>
                <input type="text" id="lname" name="lastname" placeholder="Your Email.." />


                <label>Age</label>

                <input type="text" id="fname" name="firstname" placeholder="Your Age.." />

                <button type="submit" value="Submit">Submit </button>

              </form>

            </div>
            :
            <div>
              {
                this.state.loading !== true ?
                  <table className = 'user-info'>
                   <thead>
                   <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Age</th>
                    </tr>
                   </thead>
                    {
                      userInfo.map(user =><tbody>
                                                <tr>
                                                  <td>{user.name}</td>
                                                  <td>{user.email}</td>
                                                  <td>{user.age}</td>
                                                  <td><button>Edit</button></td>
                                                  <td><button>Delete</button></td>
                                                </tr>
                                          </tbody>)
                    }

                  </table>

                :
                <div className = 'loading'>
                    <p>Loading.....</p>
                </div>
              }

            </div>
          }

        </main>



      </div>
    )
  }
}

export default App;
