import React, { Component } from 'react'

export class ListOfItems extends Component {
    constructor(props){
        super(props)
        this.state = {
            person : this.props.list
        }
    
        this.onClickDelete = this.onClickDelete.bind(this);
    }

    getAllTheUser(){
        const url = "http://localhost:8000/users";
        
        axios.get(url)
        .then(res => {
          this.setState({
            loading : false,
            person: res.data
          })
          console.log('call');
        })
        .catch(err => console.log(err));
    }

      
    onClickDelete(e){
        const url = 'http://localhost:8000/users/' + String(e);
    
        axios.delete(url)
        .then(res => {
          alert('Entry has been deleted');
          this.getAllTheUser();
        })
        .catch(err => console.log(err));
      }
    
    render() {
        return (
            <div>
                <table className = 'user-info'>
                   <thead>
                   <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Age</th>
                    </tr>
                   </thead>
                    {
                      this.state.person.map(user =><tbody key = {user._id}>
                                                <tr>
                                                  <td>{user.name}</td>
                                                  <td>{user.email}</td>
                                                  <td>{user.age}</td>
                                                  <td><button onClick = {() => this.onClickEdit(user)}>Edit</button></td>
                                                  <td><button onClick = {() => this.onClickDelete(user._id)}>Delete!</button></td>
                                                </tr>
                                          </tbody>)
                    }

                  </table>

                
            </div>
        )
    }
}

export default ListOfItems
