import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      position : 'main',
      loading : true,
      person : null
    }

    this.changePositionToUser = this.changePositionToUser.bind(this);
    this.changePositionToMain = this.changePositionToMain.bind(this);
  }

  async componentDidMount() {
    const url = "http://localhost:8000/users";

    axios.get(url).then(res => {
      console.log(res.data);
    })
  }

  changePositionToUser(){
    this.setState({position : 'user'});
  }

  changePositionToMain(){
    this.setState({position : 'main'});
  }

  render() {
    return (
      <div className = 'App'>
        <nav>
          <h1>Welcome to crud App</h1>
          {
              this.state.position === 'main' ?
              <div>
                  <p className = 'to-the-left' onClick = {this.changePositionToUser}>Check all User</p>
              </div>
              :
              <div>
                  <p className = 'to-the-left' onClick = {this.changePositionToMain}> Back to Main Page</p>
              </div>


          };
        </nav>
        <main>
          {
            this.state.position === 'main' ? 
            <div className = 'content'>
              <form >
                <label htmlFor="fname">Full Name</label>
                <input type="text" id="fname" name="firstname" placeholder="Your Full-Name.." />

                <label htmlFor="lname">Email</label>
                <input type="text" id="lname" name="lastname" placeholder="Your Email.." />


                <label htmlFor="fname">Age</label>

                <input type="text" id="fname" name="firstname" placeholder="Your Age.." />

                <input type="submit" value="Submit" />

              </form>

            </div>
            :
            <h1>Abhi poora kr rhe</h1>
          }

        </main>



      </div>
    )
  }
}

export default App;
