import React, { Component } from 'react';
import ListOfItems from './ListOfItems.js';
import axios from 'axios';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = { 
      position : 'main',
      loading : true,
      person : [],
      name : null,
      email: null,
      age : null

    }

    this.changePositionToUser = this.changePositionToUser.bind(this);
    this.changePositionToMain = this.changePositionToMain.bind(this);
    this.onSubmitFormButton = this.onSubmitFormButton.bind(this);
    this.onChangeFullName = this.onChangeFullName.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this); 
    this.onChangeAge = this.onChangeAge.bind(this); 
    this.onClickDelete = this.onClickDelete.bind(this);
  }

  getAllTheUser(){
    const url = "http://localhost:8000/users";

    axios.get(url)
    .then(res => {
      this.setState({
        loading : false,
        person: res.data
      })
      console.log('call');
    })
    .catch(err => console.log(err));
  }



  async componentDidMount() {
    this.getAllTheUser();
  }

  changePositionToUser(){
    this.setState({position : 'user'});
  }

  changePositionToMain(){
    this.setState({position : 'main'});
  }

  onChangeFullName(event){
    this.setState({name : event.target.value})
  }

  onChangeEmail(event){
    this.setState({email : event.target.value})
  }

  onChangeAge(event){
    this.setState({age : event.target.value})
  }

  onSubmitFormButton(event){
    if(this.state.name === null || this.state.email === null || this.state.age === null){
      alert('Some Information is missing');
      return ;
    }    

    axios.post('http://localhost:8000/users', {
      name : this.state.name,
      age : this.state.age,
      email : this.state.email
    }).then(res => console.log(res))
    .catch(err => console.log(err));

    alert(`${this.state.name} has been added to database Click the All user button to check!`);


    this.setState({
      person : [],
      name : null,
      email: null,
      age : null
    })


  }


  onClickDelete(e){
    const url = 'http://localhost:8000/users/' + String(e);

    axios.delete(url)
    .then(res => {
      alert('Entry has been deleted');
      this.getAllTheUser();
    })
    .catch(err => console.log(err));
  }


  render() {

    return (
      <div className = 'App'>
        <nav>
          <h1>Welcome to Crud-App</h1>
          {
              this.state.position === 'main' ?
              <div>
                  <p className = 'to-the-left' onClick = {this.changePositionToUser}>Check all User</p>
              </div>
              :
              <div>
                  <p className = 'to-the-left' onClick = {this.changePositionToMain}> Back to Main Page</p>
              </div>


          };
        </nav>
        <main>
          {
            this.state.position === 'main' ? 
            <div className = 'content'>
              <form onSubmit= {this.onSubmitFormButton}>
                <label >Full Name</label>
                <input type="text"  placeholder="Your Full-Name.." onChange = {this.onChangeFullName} />

                <label >Email</label>
                <input type="text"  placeholder="Your Email.." onChange = {this.onChangeEmail} />


                <label>Age</label>
                <input type="number"  placeholder="Your Age.." onChange = {this.onChangeAge}  />

                <button type="submit" value="Submit">Submit </button>

              </form>

            </div>
            :
            <div>
              {
                this.state.loading !== true && this.state.position === 'user' && this.state.person.length !== 0?
                  <ListOfItems list = {this.state.person} />
                :
                <div className = 'empty-entry'>
                    <p>There is no entry Please add some of the entry</p>
                </div>
              }

            </div>
          }

        </main>



      </div>
    )
  }
}

export default App;
